    [LibraryImport("SDL3", EntryPoint = "iconv", StringMarshalling = StringMarshalling.Utf8)]
    [UnmanagedCallConv(CallConvs = [typeof(CallConvCdecl)])]
    public static partial uint iconv(nint cd, nuint inbuf, uint* inbytesleft, nuint outbuf, uint* outbytesleft);

    public static unsafe uint iconvString(nint cd, out string? inbuf, uint* inbytesleft, out string? outbuf, uint* outbytesleft)
    {
        nuint inbufPtr = 0;
        nuint outbufPtr = 0;
        uint result = iconv(cd, (nuint)(&inbufPtr), inbytesleft, (nuint)(&outbufPtr), outbytesleft);
        if (inbufPtr == 0)
        {
            inbuf = null;
        }
        else
        {
            inbuf = Marshal.PtrToStringUTF8((nint)inbufPtr);
        }
        if (outbufPtr == 0)
        {
            outbuf = null;
        }
        else
        {
            outbuf = Marshal.PtrToStringUTF8((nint)outbufPtr);
        }
        return result;
    }

